import pandas as pd
import matplotlib.pyplot as plt
import base64
from io import BytesIO

data = pd.DataFrame(data_json)

# Convert Date to datetime objects
data['Date'] = pd.to_datetime(data['Date'], format='%d/%m/%Y')

# Extract Year from Date
data['Year'] = data['Date'].dt.year

# Filter for holidays
holiday_data = data[data['IsHoliday'] == True]

# Group by year and holiday
holiday_sales = holiday_data.groupby(['Year', 'Date'])['MarkDown3'].sum().reset_index()

# Create the bar chart
plt.figure(figsize=(10, 6))
plt.bar(holiday_sales['Date'].dt.strftime('%Y-%m-%d'), holiday_sales['MarkDown3'])
plt.xlabel('Date')
plt.ylabel('MarkDown3')
plt.title('MarkDown3 for Holidays Across Years')
plt.xticks(rotation=45)
plt.tight_layout()

# Save the chart to a BytesIO object
image_data = BytesIO()
plt.savefig(image_data, format='png')

# Encode the image in base64
base64_image = 'data:image/png;base64,' + base64.b64encode(image_data.getvalue()).decode('utf-8')

print(base64_image)